import React, { useState   } from 'react';
import styled from 'styled-components'
import Lamp from './Lamp'
import LightSwitch from './LightSwitch'

const Room = styled.div`
  position:relative;
  width:500px;
  height:500px;
  border:10px solid black;
  margin:0 auto;
  `;


export default function App() {

  const [lights, setLights] = useState({one:true,two:false})

  const handleLights = (idx) => setLights(...lights,l)

  return (
    <Room>
      <Lamp lampOn={lights.one} pos="left" />
      <Lamp lampOn={lights.two} pos="right" />
      <LightSwitch 
        name='one'
        callback={handleLights}
        switchOn  = {lights.one}
        pos = 'left'
      />
      <LightSwitch 
        name='two'
        callback={handleLights}
        switchOn  = {lights.two}
        pos = 'right'
      />
    </Room>

  )
}
