import React, {useState, useContext} from 'react'
import { GlobalContext } from '../context/GlobalState';

export const AddTransaction = () => {
  const [text, setText] = useState('');
  const [amount, setAmount] = useState(0);

  const { addTransaction } = useContext(GlobalContext);

  const onSubmit = e => {
    e.preventDefault();

    const newTransaction = {
      id: Math.floor(Math.random() * 100000000),
      text,
      amount: +amount
    }

    addTransaction(newTransaction);
    const clearit = {
      id: Math.floor(Math.random() * 100000000),
      text:"",
      amount: "0"
    }

    addTransaction(clearit);
  }

  return (
    <>
      <h3>增加收支記錄</h3>
      <form onSubmit={onSubmit}>
        <div className="form-control">
          <label htmlFor="text">記錄說明</label>
          <input type="text" value={text} onChange={(e) => setText(e.target.value)} placeholder="請在這裏輸入..." />
        </div>
        <div className="form-control">
          <label htmlFor="amount"
            >金額 :(-)負號消費(+)正數收入</label>
          <input type="number" value={amount} onChange={(e) => setAmount(e.target.value)} placeholder="請輸入金額..." />
        </div>
        <button className="btn">增加</button>
      </form>
    </>
  )
}
