import React, {useState, useContext} from 'react'
import { GlobalContext } from '../context/GlobalState';

export const AddTransaction = () => {
  const [text, setText] = useState('');
  const [amount, setAmount] = useState(0);

  const { addTransaction } = useContext(GlobalContext);

  const onSubmit = e => {
    e.preventDefault();

    const newTransaction = {
      id: Math.floor(Math.random() * 100000000),
      text,
      amount: +amount
    }

    addTransaction(newTransaction);
  }

  return (
    <>
      <h3>添加的新的記錄</h3>
      <form onSubmit={onSubmit}>
        <div className="form-control">
          <label htmlFor="text">輸入交易相關信息:</label>
          <input type="text" value={text} onChange={(e) => setText(e.target.value)} placeholder="請在這裏輸入..." />
        </div>
        <div className="form-control">
          <label htmlFor="amount"
            >金額: <br />
            (負數 代表(-)消費, 正數 代表(+)收入)</label
          >
          <input type="number" value={amount} onChange={(e) => setAmount(e.target.value)} placeholder="請輸入金額..." />
        </div>
        <button className="btn">增加一條記錄</button>
      </form>
    </>
  )
}
